import Header from './components/Header'
import Tasks from './components/Tasks'

const App = () => {
  const [tasks, setTasks] = useState([
    {
        id:1,
        text: "Doctor Appointment",
        day: "Mar 1 at 12:30",
        reminder: true
    },
    {
        id:2,
        text: "Work meeting",
        day: "Feb 27 at 14:00",
        reminder: true
    },
    {
        id:3,
        text: "Sofia Dance fest 2022",
        day: "Apr 2 at 8:00",
        reminder: true
    }
  ])

  const deleteTask = (id) => {
    console.log("delete", id)
  }

  return (
    <div className="container">
      <Header />
      <Tasks 
        tasks = {tasks}
        onDelete = {deleteTask}
      />
    </div>
  );
}

export default App;
