import {useState} from 'react'

const Tasks = () => {
    const [tasks, setTasks] = useState([
        {
            id:1,
            text: "Doctor Appointment",
            day: "Mar 1 at 12:30",
            reminder: true
        },
        {
            id:2,
            text: "Work meeting",
            day: "Feb 27 at 14:00",
            reminder: true
        },
        {
            id:3,
            text: "Sofia Dance fest 2022",
            day: "Apr 2 at 8:00",
            reminder: true
        }
    ])

    return (
        <>
        {tasks.map((task) => (
            <h3 key={task.id} > {task.text} </h3>
        ))}
        </>
    )
}

export default Tasks
